    isAnagram(first, second) {
      // Convert strings to lowercase and remove any non-alphabetic characters
      const regex = /[^\w]/g;
      const str1 = first.toLowerCase().replace(regex, '');
      const str2 = second.toLowerCase().replace(regex, '');

      // Sort the characters in each string and compare them
      const sortedStr1 = str1.split('').sort().join('');
